import React from 'react';

import {Grid} from '@material-ui/core';

import {SearchBar, VideoDetail,VideoList,VideoItem} from './components'

import youtube from './api/youtube';

class App extends React.Component {
    state = {
        videos:[],
        selectedVideo:null,
    }

    componentDidMount() {this.handleSubmit('rwn underwater')

    }


    onVideoSelect =(video) => {this.setState({selectedVideo:video})

    }

    handleSubmit = async (searchTerm) => {
        const response = await youtube.get('search', {params: {
            part:'snippet',
            maxResults:5,
            key:'AIzaSyBkujhCOkUwe3HZp7-ad0umhKQ2sOKlbfo',
            q:searchTerm,
        }});

        this.setState({videos:response.data.items, selectedVideo:response.data.items[0]});
    }
    render () {
        const {selectedVideo, videos} =this.state
        return (
            <Grid justify ='center' container spacing ={10}><Grid item xs={12}><Grid container spacing={10}><Grid item xs={12}><SearchBar onFormSubmit={this.handleSubmit}/>
                </Grid>
                <Grid item xs={8}>
                    <VideoDetail video={this.state.selectedVideo}/>
                </Grid>
                <Grid item xs={4}>
                    <VideoList videos={videos} onVideoSelect={this.onVideoSelect}/>
                </Grid>
             </Grid>
            </Grid>
            </Grid>  
        )
    }
}

export default App
